import Phaser from"phaser";import Entity from"./Entity";export default class ChaserShip extends Entity{constructor(t,s,e){super(t,s,e,"sprEnemy1","ChaserShip"),this.body.velocity.y=Phaser.Math.Between(50,100),this.states={MOVE_DOWN:"MOVE_DOWN",CHASE:"CHASE"},this.state=this.states.MOVE_DOWN}update(){if(!this.getData("isDead")&&this.scene.player){if(Phaser.Math.Distance.Between(this.x,this.y,this.scene.player.x,this.scene.player.y)<320&&(this.state=this.states.CHASE),this.state===this.states.CHASE){const t=this.scene.player.x-this.x,s=this.scene.player.y-this.y,e=Math.atan2(s,t),i=100;this.body.setVelocity(Math.cos(e)*i,Math.sin(e)*i)}this.x<this.scene.player.x?this.angle-=5:this.angle+=5}}}