import Phaser from"phaser";import Entity from"./Entity";import EnemyLaser from"./EnemyLaser";export default class GunShip extends Entity{constructor(e,s,t){super(e,s,t,"sprEnemy0","GunShip"),this.body.velocity.y=Phaser.Math.Between(50,100),this.shootTimer=this.scene.time.addEvent({delay:1e3,callback:()=>{const e=new EnemyLaser(this.scene,this.x,this.y);e.setScale(this.scaleX),this.scene.enemyLasers.add(e)},callbackScope:this,loop:!0}),this.play("sprEnemy0")}onDestroy(){void 0!==this.shootTimer&&this.shootTimer&&this.shootTimer.remove(!1)}}