import Phaser from"phaser";export default class ScrollingBackground{constructor(e,t,s){this.scene=e,this.key=t,this.velocityY=s,this.layers=this.scene.add.group(),this.createLayers()}createLayers(){for(let e=0;e<2;e+=1){const t=this.scene.add.sprite(200,400,this.key);t.y=t.displayHeight*e;const s=Phaser.Math.Between(0,10)>=5?-1:1,i=Phaser.Math.Between(0,10)>=5?-1:1;t.setScale(2*s,3*i),t.setDepth(-5-(e-1)),this.scene.physics.world.enableBody(t,0),t.body.velocity.y=this.velocityY,this.layers.add(t)}}update(){if(this.layers.getChildren()[0].y>0)for(let e=0;e<this.layers.getChildren().length;e+=1){const t=this.layers.getChildren()[e];t.y=-t.displayHeight+t.displayHeight*e}}}